package com.blk.uhabits.widgets;import android.content.Context;import android.graphics.Rect;import android.util.AttributeSet;import android.view.MotionEvent;import android.view.TouchDelegate;import android.view.View;import android.widget.LinearLayout;public class SwipeViewContainer extends LinearLayout {    private boolean mBypass = false;    private SwipeDismissTouchListener mSwipeListener;    public SwipeViewContainer(Context context) {        super(context);    }    public SwipeViewContainer(Context context, AttributeSet attrs) {        super(context, attrs);    }    public SwipeViewContainer(Context context, AttributeSet attrs, int defStyleAttr) {        super(context, attrs, defStyleAttr);    }    public void setOnSwipeListener(SwipeDismissTouchListener swipeListener) {        mSwipeListener = swipeListener;    }    @Override    public boolean onInterceptTouchEvent(MotionEvent event) {        int action = event.getAction();        if (action == MotionEvent.ACTION_UP || action == MotionEvent.ACTION_CANCEL) {            mBypass = false;        }        if (mSwipeListener != null && !mBypass) {            boolean pass = mSwipeListener.onTouch(null, event);            if (action == MotionEvent.ACTION_UP) {                return pass;            }        }        return false;    }}